import React, { useState } from 'react';
import './App.css';

function App() {
    const [activeSection, setActiveSection] = useState('game');

    const sidebarItems = [
        { id: 'game', label: 'Oyun', icon: 'üéÆ' },
        { id: 'scoreboard', label: 'Skor Tablosu', icon: 'üèÜ' },
        { id: 'history', label: 'Ge√ßmi≈ü', icon: 'üìã' },
        { id: 'profile', label: 'Profil', icon: 'üë§' },
        { id: 'settings', label: 'Ayarlar', icon: '‚öôÔ∏è' }
    ];

    return (
        <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#1a1a1a' }}>
            {/* Sol Sidebar */}
            <div style={{
                width: '250px',
                backgroundColor: '#2d2d2d',
                color: 'white',
                position: 'fixed',
                height: '100vh',
                borderRight: '1px solid #444'
            }}>
                {/* Logo */}
                <div style={{ padding: '20px', borderBottom: '1px solid #444' }}>
                    <h2 style={{ margin: 0 }}>
            <span style={{
                backgroundColor: 'white',
                color: 'black',
                padding: '4px 8px',
                borderRadius: '4px',
                marginRight: '8px'
            }}>Go</span>
                        Portal
                    </h2>
                </div>

                {/* Menu */}
                <div style={{ padding: '20px' }}>
                    {sidebarItems.map((item) => (
                        <button
                            key={item.id}
                            onClick={() => setActiveSection(item.id)}
                            style={{
                                width: '100%',
                                padding: '12px',
                                margin: '4px 0',
                                backgroundColor: activeSection === item.id ? '#4a90e2' : 'transparent',
                                color: 'white',
                                border: 'none',
                                borderRadius: '6px',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                fontSize: '16px'
                            }}
                        >
                            <span style={{ marginRight: '10px' }}>{item.icon}</span>
                            {item.label}
                        </button>
                    ))}
                </div>
            </div>

            {/* Ana ƒ∞√ßerik */}
            <div style={{ marginLeft: '250px', flex: 1, padding: '20px' }}>
                <h1 style={{ color: 'white', marginBottom: '20px' }}>
                    {sidebarItems.find(item => item.id === activeSection)?.label}
                </h1>

                {activeSection === 'game' && (
                    <div style={{
                        backgroundColor: '#2d2d2d',
                        padding: '30px',
                        borderRadius: '10px',
                        color: 'white'
                    }}>
                        <h2>Go Oyunu</h2>
                        <div style={{
                            width: '400px',
                            height: '400px',
                            backgroundColor: '#4a90e2',
                            borderRadius: '10px',
                            display: 'grid',
                            gridTemplateColumns: 'repeat(8, 1fr)',
                            gap: '2px',
                            padding: '10px'
                        }}>
                            {Array(64).fill().map((_, index) => (
                                <div
                                    key={index}
                                    style={{
                                        backgroundColor: '#6bb6ff',
                                        borderRadius: '2px',
                                        cursor: 'pointer'
                                    }}
                                    onClick={() => console.log(`Kare ${index} tƒ±klandƒ±`)}
                                />
                            ))}
                        </div>
                    </div>
                )}

                {activeSection !== 'game' && (
                    <div style={{
                        backgroundColor: '#2d2d2d',
                        padding: '30px',
                        borderRadius: '10px',
                        color: 'white'
                    }}>
                        <p>Bu b√∂l√ºm hen√ºz geli≈ütirilmekte...</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
