import React, { useState } from 'react';
import './App.css';

function App() {
    const [activeSection, setActiveSection] = useState('game');

    const sidebarItems = [
        { id: 'game', label: 'Oyun', icon: 'üéÆ' },
        { id: 'scoreboard', label: 'Skor Tablosu', icon: 'üèÜ' },
        { id: 'history', label: 'Ge√ßmi≈ü', icon: 'üìã' },
        { id: 'profile', label: 'Profil', icon: 'üë§' },
        { id: 'settings', label: 'Ayarlar', icon: '‚öôÔ∏è' }
    ];

    return (
        <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#1a1a1a' }}>
            {/* Sol Sidebar */}
            <div style={{
                width: '250px',
                backgroundColor: '#2d2d2d',
                color: 'white',
                position: 'fixed',
                height: '100vh',
                borderRight: '1px solid #444'
            }}>
                {/* Logo */}
                <div style={{ padding: '20px', borderBottom: '1px solid #444' }}>
                    <h2 style={{ margin: 0 }}>
            <span style={{
                backgroundColor: 'white',
                color: 'black',
                padding: '4px 8px',
                borderRadius: '4px',
                marginRight: '8px'
            }}>Go</span>
                        Portal
                    </h2>
                </div>

                {/* Menu */}
                <div style={{ padding: '20px' }}>
                    {sidebarItems.map((item) => (
                        <button
                            key={item.id}
                            onClick={() => setActiveSection(item.id)}
                            style={{
                                width: '100%',
                                padding: '12px',
                                margin: '4px 0',
                                backgroundColor: activeSection === item.id ? '#4a90e2' : 'transparent',
                                color: 'white',
                                border: 'none',
                                borderRadius: '6px',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                fontSize: '16px'
                            }}
                        >
                            <span style={{ marginRight: '10px' }}>{item.icon}</span>
                            {item.label}
                        </button>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
